$(document).ready(function () {
    $(".zero-configuration").DataTable({
        // Custom DOM layout agar pagination bisa diatur
        dom:
            "<'row'<'col-sm-12 col-md-6'l><'col-sm-12 col-md-6'f>>" +
            "<'row'<'col-sm-12'tr>>" +
            "<'row'<'col-sm-12 col-md-5'i><'col-sm-12 col-md-7'p>>",

        // Ganti pagination dengan Bootstrap style
        pagingType: "simple_numbers",
        language: {
            lengthMenu: "Tampilkan _MENU_ entri",
            paginate: {
                first: "Pertama",
                previous: "Sebelum",
                next: "Lanjut",
                last: "Terakhir",
            },
            search: "Cari:",
            info: "Menampilkan _START_ sampai _END_ dari _TOTAL_ entri",
        },

        drawCallback: function () {
            $(".dataTables_wrapper").css({
                margin: "0",
                padding: "0",
            });
            $(".dataTables_paginate .pagination").addClass(
                "justify-content-end"
            );
            $(".dataTables_paginate .paginate_button")
                .removeClass("paginate_button")
                .addClass("page-item");
            $(".dataTables_paginate .paginate_button a")
                .addClass("page-link")
                .css("border-radius", "5px");
            $(".dataTables_paginate .paginate_button.previous a").text(
                "Sebelum"
            );
            $(".dataTables_paginate .paginate_button.next a").text("Lanjut");
            $(".dataTables_paginate .paginate_button.first a").text("Pertama");
            $(".dataTables_paginate .paginate_button.last a").text("Terakhir");
        },
    });

    // Tambahkan styling tambahan untuk input dan dropdown
    $(".dataTables_length select").css({
        width: "auto",
        height: "auto",
        "border-radius": "5px",
        border: "1px solid #ced4da",
    });
    $(".dataTables_filter input").css({
        height: "auto",
        "border-radius": "5px",
        border: "1px solid #ced4da",
    });
    $(".dataTables_wrapper .table-bordered").css({
        "border-radius": "5px",
    });
});
